double HmsToDeg(float H, float M, float S) {  //OK
    double DGR;
    DGR = fabs(H) * 15+ M * 15 / 60 + S * 15 / 3600;
    if (H<0) DGR = -DGR;
    return(DGR);
}

double DmsToDeg(float D, float M, float S) {  //OK
    double DGR;
    DGR = fabs(D) + M / 60 + S / 3600;
    if (D<0) DGR=-DGR;
    return(DGR);
}

double DmToDeg(double D,double M, double Dec) {
    double Deg;
    Deg = D+(M+Dec/10)/60;
    return(Deg);
}

