double RAfromECL(double lambda, double beta, double E){
    double ra;
    double a,b,c,d,e;
    bool big_lamda = 0, west_lambda = 1;
    while (lambda<0) lambda = lambda+360.0;
    a = sind(lambda);
    e = cosd(lambda);
    b = cosd(E);
    c = sind(beta)/cosd(beta);
    d = sind(E);
    ra = (a*b-c*d)/e;
//    cout<<endl<<"tg(ra) = "<<ra<<endl;;
//   cout<<a<<endl<<b<<endl<<c<<endl<<d<<endl<<e<<endl;
    if (ra<0) ra = 180-atand(-ra);
    else ra = atand(ra);
    if (lambda>180) ra = ra+180.0;
    if (ra>360.0) ra = ra-360.0;
    return(ra);
}

double DECLfromECL(double lambda, double beta, double E){
    double decl;
    decl = sind(beta)*cosd(E)+cosd(beta)*sind(E)*sind(lambda);
    decl =asind(decl);
    return(decl);
}

