double findE(double M, double e) {
    double E0,E1,corr;
    E0 = M;
    corr = (M+e*sind(E0)-E0)/(1-(e*180.0/pi)*cosd(E0));
    E1 = E0+corr;
    while (abs(corr)>0.0000001){
    E0 = E1;
    corr = (M+(e*180.0/pi)*sind(E0)-E0)/(1-e*cosd(E0));
    E1 = E0+corr;
    }
    return(E1);
}

