float findPcosL(float H,float lat){ //OK
    float u;
    if (lat<0) u = 0.99664719*(180.0-tand(-lat));
    else u = 0.99664719*(tand(lat));
    u = atand(u);
    return(cosd(u)+H/6378140.0*cosd(lat));
}

float findPsinL(float H,float lat) { //OK
    float u;
    if (lat<0) u = 0.99664719*(180.0-tand(-lat));
    else u = 0.99664719*(tand(lat));
    u = atand(u);
    return(0.99664719*sind(u)+H/6378140.0*sind(lat));
}

