int CaseNum(char num){
    switch (num) {
        case '1': return(1);
        case '2': return(2);
        case '3': return(3);
        case '4': return(4);
        case '5': return(5);
        case '6': return(6);
        case '7': return(7);
        case '8': return(8);
        case '9': return(9);
        case '0': return(0);
        case '.': return(-1);
    }
}

double TypeLat(char lat[7]){
    int i,a;
    double b, res;
    i = 0;
    while (lat[i]!=' ') {
        a = a*10 + CaseNum(lat[i]);
        i++;
    }
    i++;
    while ((lat[i]!='S')||(lat[i]!='N')){
        if (CaseNum(lat[i])!=-1) b = b*10 + CaseNum(lat[i])/60;
        else {
            i++;
            b = b + CaseNum(lat[i])/600;
        }
        i++;
    }
    res = a+b;
    if (lat[i]='S') res = -res;
    return(res);
}

